package com.example.model;

import java.util.Date;

/**
 * Entity representing a Convention.
 */
public class Convention {
    private int id;
    private int pointOfRestId;
    private int agencyId;
    private Date startDate;
    private Date endDate;
    private String terms;
    private ConventionStatus status;

    /**
     * Constructor for creating a new convention (ID will be generated by repository).
     */
    public Convention(int pointOfRestId, int agencyId, Date startDate, Date endDate, String terms) {
        this.pointOfRestId = pointOfRestId;
        this.agencyId = agencyId;
        this.startDate = startDate;
        this.endDate = endDate;
        this.terms = terms;
        this.status = ConventionStatus.PENDING; // Default status
    }

    // Getters and setters
    public int getId() {
        return id;
    }

    public void setId(int id) {
        this.id = id;
    }

    public int getPointOfRestId() {
        return pointOfRestId;
    }

    public int getAgencyId() {
        return agencyId;
    }

    public Date getStartDate() {
        return startDate;
    }

    public Date getEndDate() {
        return endDate;
    }

    public String getTerms() {
        return terms;
    }

    public ConventionStatus getStatus() {
        return status;
    }

    public void setStatus(ConventionStatus status) {
        this.status = status;
    }
}