 * Contains business logic for tourist account management, including
 * the complete ModifyTouristAccountData use case workflow.
 */
@Service
@RequiredArgsConstructor
@Slf4j
public class TouristServiceImpl implements TouristService {

    private final TouristRepository touristRepository;
    private final ValidationService validationService;
    private final AuditService auditService;
    private final ETOURServerClient etourServerClient;
    private final RetryUtil retryUtil;

    private static final String UPDATE_TOURIST_ACTION = "UPDATE_TOURIST";
    private static final String CREATE_TOURIST_ACTION = "CREATE_TOURIST";
    private static final String CHANGE_STATUS_ACTION = "CHANGE_TOURIST_STATUS";

    @Override
    public TouristDTO getTouristById(Long id) {
        log.debug("Retrieving tourist with ID: {}", id);
        Tourist tourist = touristRepository.findById(id)
                .orElseThrow(() -> new TouristNotFoundException("Tourist not found with ID: " + id));
        return TouristDTO.fromEntity(tourist);
    }

    @Override
    public TouristDTO getTouristByEmail(String email) {
        log.debug("Retrieving tourist with email: {}", email);
        Tourist tourist = touristRepository.findByEmail(email)
                .orElseThrow(() -> new TouristNotFoundException("Tourist not found with email: " + email));
        return TouristDTO.fromEntity(tourist);
    }

    @Override
    public List<TouristDTO> searchTourists(String firstName, String lastName, String email, 
                                          String nationality, Boolean active) {
        log.debug("Searching tourists with criteria: firstName={}, lastName={}, email={}, nationality={}, active={}",
                firstName, lastName, email, nationality, active);

        List<Tourist> tourists = touristRepository.searchTourists(firstName, lastName, email, nationality, active);
        return tourists.stream()
                .map(TouristDTO::fromEntity)
                .collect(Collectors.toList());
    }

    @Override
    public TouristResponse updateTourist(Long id, TouristUpdateRequest updateRequest, 
                                        String operatorUsername, HttpServletRequest request) {
        log.info("Updating tourist ID: {} by operator: {}", id, operatorUsername);

        // Step 1: Retrieve the tourist
        Tourist tourist = touristRepository.findById(id)
                .orElseThrow(() -> new TouristNotFoundException("Tourist not found with ID: " + id));

        TouristDTO original = TouristDTO.fromEntity(tourist);

        // Step 2: Validate the update request
        ValidationErrorDTO validationErrors = validateTouristUpdate(id, updateRequest);
        if (validationErrors.hasErrors()) {
            log.warn("Validation failed for tourist update ID {}: {}", id, validationErrors.getErrorSummary());
            auditService.logFailedAction(UPDATE_TOURIST_ACTION, "Tourist", id, operatorUsername, 
                    validationErrors.getErrorSummary(), getClientInfo(request));
            return TouristResponse.validationError(validationErrors);
        }

        // Step 3: Check if there are actual changes
        if (!updateRequest.hasChanges(original)) {
            log.info("No changes detected for tourist ID: {}", id);
            return TouristResponse.success("No changes required", original);
        }

        // Step 4: Prepare confirmation response
        Map<String, Object> changes = updateRequest.getChangedValuesMap(original);
        Map<String, Object> confirmationData = new HashMap<>();
        confirmationData.put("touristId", id);
        confirmationData.put("original", original);
        confirmationData.put("changes", changes);
        confirmationData.put("confirmationToken", generateConfirmationToken(id, changes));

        log.info("Update confirmation required for tourist ID: {} with changes: {}", id, updateRequest.getUpdateSummary());
        return TouristResponse.confirmationRequired("Please confirm the changes", confirmationData);
    }

    @Override
    public ValidationErrorDTO validateTouristUpdate(Long id, TouristUpdateRequest updateRequest) {
        log.debug("Validating update request for tourist ID: {}", id);

        ValidationErrorDTO errors = new ValidationErrorDTO();

        // Check if update request is empty
        if (updateRequest.isEmpty()) {
            errors.addGlobalError("Update request is empty");
            return errors;
        }

        // Validate email uniqueness if being changed
        if (updateRequest.getEmail() != null && !isEmailAvailable(updateRequest.getEmail(), id)) {
            errors.addFieldError("email", updateRequest.getEmail(), 
                    "Email is already in use by another tourist");
        }

        // Validate passport number uniqueness if being changed
        if (updateRequest.getPassportNumber() != null && 
            !isPassportNumberAvailable(updateRequest.getPassportNumber(), id)) {
            errors.addFieldError("passportNumber", updateRequest.getPassportNumber(), 
                    "Passport number is already in use by another tourist");
        }

        // Validate individual fields using validation service
        if (updateRequest.getEmail() != null) {
            ValidationErrorDTO emailErrors = validationService.validateEmail(updateRequest.getEmail());
            errors.getFieldErrors().addAll(emailErrors.getFieldErrors());
        }

        if (updateRequest.getPhoneNumber() != null) {
            ValidationErrorDTO phoneErrors = validationService.validatePhoneNumber(updateRequest.getPhoneNumber());
            errors.getFieldErrors().addAll(phoneErrors.getFieldErrors());
        }

        if (updateRequest.getDateOfBirth() != null && updateRequest.getDateOfBirth().isAfter(java.time.LocalDate.now())) {
            errors.addFieldError("dateOfBirth", updateRequest.getDateOfBirth(), 
                    "Date of birth cannot be in the future");
        }

        return errors;
    }

    @Override
    public TouristResponse prepareUpdateConfirmation(Long id, TouristUpdateRequest updateRequest) {
        // This is essentially the same logic as the confirmation step in updateTourist
        // but separated for clarity in the use case flow
        Tourist tourist = touristRepository.findById(id)
                .orElseThrow(() -> new TouristNotFoundException("Tourist not found with ID: " + id));

        TouristDTO original = TouristDTO.fromEntity(tourist);
        Map<String, Object> changes = updateRequest.getChangedValuesMap(original);

        Map<String, Object> confirmationData = new HashMap<>();
        confirmationData.put("touristId", id);
        confirmationData.put("original", original);
        confirmationData.put("changes", changes);
        confirmationData.put("confirmationToken", generateConfirmationToken(id, changes));

        return TouristResponse.confirmationRequired("Confirm tourist account changes", confirmationData);
    }

    @Override
    @Transactional
    public TouristResponse confirmAndUpdateTourist(Long id, TouristUpdateRequest updateRequest,
                                                  String operatorUsername, HttpServletRequest request,
                                                  String confirmationToken) {
        log.info("Confirming and applying update for tourist ID: {} by operator: {}", id, operatorUsername);

        // Verify confirmation token (idempotency check)
        Tourist tourist = touristRepository.findById(id)
                .orElseThrow(() -> new TouristNotFoundException("Tourist not found with ID: " + id));

        TouristDTO original = TouristDTO.fromEntity(tourist);
        Map<String, Object> changes = updateRequest.getChangedValuesMap(original);
        String expectedToken = generateConfirmationToken(id, changes);

        if (confirmationToken != null && !confirmationToken.equals(expectedToken)) {
            log.warn("Invalid confirmation token for tourist ID: {}", id);
            return TouristResponse.error("Invalid confirmation token", 400, null);
        }

        // Re-validate (in case request was modified)
        ValidationErrorDTO validationErrors = validateTouristUpdate(id, updateRequest);
        if (validationErrors.hasErrors()) {
            log.error("Validation failed during confirmation for tourist ID {}: {}", 
                    id, validationErrors.getErrorSummary());
            auditService.logFailedAction(UPDATE_TOURIST_ACTION, "Tourist", id, operatorUsername, 
                    validationErrors.getErrorSummary(), getClientInfo(request));
            return TouristResponse.validationError(validationErrors);
        }

        try {
            // Step 5: Apply the changes
            tourist.updateFrom(updateRequest);
            tourist.validate(); // Ensure entity is still valid after update

            // Step 6: Store modified data
            Tourist savedTourist = touristRepository.save(tourist);
            TouristDTO updated = TouristDTO.fromEntity(savedTourist);

            // Step 6a: Sync with ETOUR server (with retry logic)
            syncWithETOURServer(updated, operatorUsername, request);

            // Audit successful update
            auditService.logSuccessfulAction(UPDATE_TOURIST_ACTION, "Tourist", id, operatorUsername, 
                    original, updated, getClientInfo(request));

            log.info("Successfully updated tourist ID: {}", id);
            return TouristResponse.updateSuccess(updated);

        } catch (ValidationException e) {
            log.error("Validation exception during tourist update ID {}: {}", id, e.getMessage());
            auditService.logFailedAction(UPDATE_TOURIST_ACTION, "Tourist", id, operatorUsername, 
                    e.getMessage(), getClientInfo(request));
            return TouristResponse.error("Validation failed: " + e.getMessage(), 400, null);

        } catch (ETourConnectionException e) {
            log.error("ETOUR connection failed during tourist update ID {}: {}", id, e.getMessage());
            // Note: Tourist update was successful locally, but ETOUR sync failed
            // We should still return success but log the ETOUR failure
            TouristDTO updated = TouristDTO.fromEntity(tourist);
            auditService.logActionWithWarning(UPDATE_TOURIST_ACTION, "Tourist", id, operatorUsername, 
                    original, updated, "ETOUR sync failed: " + e.getMessage(), getClientInfo(request));

            return TouristResponse.success(
                    "Tourist updated locally, but ETOUR sync failed: " + e.getMessage(), 
                    updated);

        } catch (Exception e) {
            log.error("Unexpected error during tourist update ID {}: {}", id, e.getMessage(), e);
            auditService.logFailedAction(UPDATE_TOURIST_ACTION, "Tourist", id, operatorUsername, 
                    e.getMessage(), getClientInfo(request));
            return TouristResponse.serverError("Internal server error during update");
        }
    }

    @Override
    @Transactional
    public TouristResponse setTouristActiveStatus(Long id, Boolean active, 
                                                 String operatorUsername, HttpServletRequest request) {
        log.info("Setting active status for tourist ID: {} to {} by operator: {}", 
                id, active, operatorUsername);

        Tourist tourist = touristRepository.findById(id)
                .orElseThrow(() -> new TouristNotFoundException("Tourist not found with ID: " + id));

        TouristDTO original = TouristDTO.fromEntity(tourist);

        if (tourist.getActive() == active) {
            log.info("Tourist ID {} already has active status: {}", id, active);
            return TouristResponse.success("No change required", original);
        }

        tourist.setActive(active);
        tourist.setUpdatedAt(LocalDateTime.now());
        tourist = touristRepository.save(tourist);

        TouristDTO updated = TouristDTO.fromEntity(tourist);

        // Audit the status change
        Map<String, Object> oldValues = new HashMap<>();
        oldValues.put("active", original.getActive());

        Map<String, Object> newValues = new HashMap<>();
        newValues.put("active", active);

        auditService.logAction(CHANGE_STATUS_ACTION, "Tourist", id, operatorUsername, 
                oldValues, newValues, getClientInfo(request));

        // Sync with ETOUR server
        try {
            syncWithETOURServer(updated, operatorUsername, request);
        } catch (ETourConnectionException e) {
            log.warn("ETOUR sync failed for status change of tourist ID {}: {}", id, e.getMessage());
        }

        String message = active ? "Tourist account activated successfully" 
                               : "Tourist account deactivated successfully";
        return TouristResponse.success(message, updated);
    }

    @Override
    public List<Map<String, Object>> getTouristChangeHistory(Long id, int limit) {
        log.debug("Getting change history for tourist ID: {} (limit: {})", id, limit);

        if (!touristExists(id)) {
            throw new TouristNotFoundException("Tourist not found with ID: " + id);
        }

        return auditService.getEntityChangeHistory("Tourist", id, limit);
    }

    @Override
    @Transactional
    public TouristResponse createTourist(TouristDTO touristDTO, String operatorUsername, 
                                        HttpServletRequest request) {
        log.info("Creating new tourist by operator: {}", operatorUsername);

        // Validate tourist data
        ValidationErrorDTO validationErrors = validationService.validateTourist(touristDTO);
        if (validationErrors.hasErrors()) {
            log.warn("Validation failed for tourist creation: {}", validationErrors.getErrorSummary());
            auditService.logFailedAction(CREATE_TOURIST_ACTION, "Tourist", null, operatorUsername, 
                    validationErrors.getErrorSummary(), getClientInfo(request));
            return TouristResponse.validationError(validationErrors);
        }

        try {
            Tourist tourist = touristDTO.toEntity();
            tourist = touristRepository.save(tourist);
            TouristDTO created = TouristDTO.fromEntity(tourist);

            // Sync with ETOUR server
            syncWithETOURServer(created, operatorUsername, request);

            // Audit creation
            auditService.logSuccessfulAction(CREATE_TOURIST_ACTION, "Tourist", tourist.getId(), 
                    operatorUsername, null, created, getClientInfo(request));

            log.info("Successfully created tourist ID: {}", tourist.getId());
            return TouristResponse.success("Tourist account created successfully", created);

        } catch (ETourConnectionException e) {
            log.error("ETOUR connection failed during tourist creation: {}", e.getMessage());
            // Note: Tourist was created locally, but ETOUR sync failed
            return TouristResponse.error("Tourist created locally, but ETOUR sync failed", 207, null);

        } catch (Exception e) {
            log.error("Unexpected error during tourist creation: {}", e.getMessage(), e);
            auditService.logFailedAction(CREATE_TOURIST_ACTION, "Tourist", null, operatorUsername, 
                    e.getMessage(), getClientInfo(request));

