package com.school;

/**
 * Entity class representing a Class.
 */
public class Class {
    private Long id;
    public String name;
    public String address;
    public int academicYear;

    public Class(String name, String address, int academicYear) {
        this.name = name;
        this.address = address;
        this.academicYear = academicYear;
        // In a real application, ID would be generated by the database.
        this.id = System.currentTimeMillis(); // simple ID generation for demo
    }

    public Long getId() {
        return id;
    }

    public String getName() {
        return name;
    }

    public String getAddress() {
        return address;
    }

    public int getAcademicYear() {
        return academicYear;
    }
}